package fr.ippon.rh.service;

import org.jasypt.util.text.BasicTextEncryptor;
import org.springframework.stereotype.Service;

/**
 * Etape 2 du jeu.
 */
@Service
public class Etape2Service {

    private static final String etape2Texte = "";

    private static final String etape2MotDePasse = "IppEvent";
            //"Vous devez avoir résolu l'étape 1 pour avoir le bon mot de passe !";

    /**
     * Décrypte le texte de l'étape 2.
     */
    public String decriptText() {
        BasicTextEncryptor textEncryptor = new BasicTextEncryptor();
        textEncryptor.setPassword(etape2MotDePasse);
        return textEncryptor.decrypt(etape2Texte);
    }
}
